(lambda _: kAssert(kFetch({"name": "Alice"}, ["name"]) == "Alice"))(None)
(lambda _: kAssert(kFetch({"user": {"name": "Bob"}}, ["user", "name"]) == "Bob"))(None)
(lambda _: kAssert(kFetch({"items": [10, 20, 30]}, ["items", 0]) == 10))(None)
(lambda _: kAssert(kFetch({"items": [10, 20, 30]}, ["items", 2]) == 30))(None)
(lambda _: kAssert(kFetch({"list": [{"id": 1}, {"id": 2}]}, ["list", 0, "id"]) == 1))(None)
(lambda _: kAssert((kFetch({"name": "Alice"}, ["missing"]) is None)))(None)
(lambda _: kAssert((kFetch({"name": "Alice"}, ["name", "child"]) is None)))(None)
(lambda _: kAssert((kFetch(None, ["x"]) is None)))(None)
(lambda _: kAssert(kFetchObject({"a": 1, "b": 2}, {"x": ["a"], "y": ["b"]}) == {"x": 1, "y": 2}))(None)
(lambda _: kAssert(kFetchObject({"foo": {"bar": 10}}, {"v": ["foo", "bar"]}) == {"v": 10}))(None)
(lambda _: kAssert(kFetchObject({"items": [1, 2, 3]}, {"first": ["items", 0], "last": ["items", 2]}) == {"first": 1, "last": 3}))(None)
(lambda _: kAssert(kFetchArray({"a": 1, "b": 2}, [["a"], ["b"]]) == [1, 2]))(None)
(lambda _: kAssert(kFetchArray({"foo": {"bar": 10}}, [["foo", "bar"]]) == [10]))(None)
(lambda _: kAssert(kFetchArray({"items": [1, 2, 3]}, [["items", 0], ["items", 1], ["items", 2]]) == [1, 2, 3]))(None)
