(lambda _: kAssert(kTypeOf(lambda : 42) == "Function"))(None)
(lambda _: kAssert(kTypeOf(lambda : 2 * 3) == "Function"))(None)
(lambda _: kAssert(kTypeOf(lambda x: x) == "Function"))(None)
(lambda _: kAssert(kTypeOf(lambda x: x * 2) == "Function"))(None)
(lambda _: kAssert(kTypeOf(lambda x, y: x + y) == "Function"))(None)
(lambda _: kAssert(kTypeOf(lambda a, b, c: a + b + c) == "Function"))(None)
(lambda _: kAssert(kTypeOf(lambda x: x * x + 1) == "Function"))(None)
(lambda _: kAssert(kTypeOf(lambda price: price * 1.21) == "Function"))(None)
(lambda _: kAssert(kTypeOf(lambda _: _.get("budget") * 1.21) == "Function"))(None)
(lambda _: kAssert(kTypeOf(lambda item: item.get("price") + item.get("tax")) == "Function"))(None)
(lambda _: kAssert(kTypeOf(lambda x: (doubled := x * 2, doubled + 1)[-1]) == "Function"))(None)
(lambda _: kAssert(kTypeOf(lambda x: (x) if (x > 0) else (0 - x)) == "Function"))(None)
(lambda _: kAssert((double := lambda x: x * 2, double(5))[-1] == 10))(None)
(lambda _: kAssert((add := lambda x, y: x + y, add(3, 4))[-1] == 7))(None)
(lambda _: kAssert((double := lambda x: x * 2, double(2))[-1] == 4))(None)
