(lambda _: kAssert(kEq(list(map(lambda x: x * 2, [1, 2, 3])), [2, 4, 6])))(None)
(lambda _: kAssert(kEq(list(filter(lambda x: x > 2, [1, 2, 3, 4])), [3, 4])))(None)
(lambda _: kAssert(functools.reduce(lambda acc, x: acc + x, [1, 2, 3], 0) == 6))(None)
(lambda _: kAssert(any(map(lambda x: x > 2, [1, 2, 3])) == True))(None)
(lambda _: kAssert(any(map(lambda x: x > 5, [1, 2, 3])) == False))(None)
(lambda _: kAssert(all(map(lambda x: x > 0, [1, 2, 3])) == True))(None)
(lambda _: kAssert(all(map(lambda x: x > 2, [1, 2, 3])) == False))(None)
(lambda _: kAssert(functools.reduce(lambda acc, x: acc + x, ["a", "b", "c"], "") == "abc"))(None)
(lambda _: kAssert(kEq(list(map(lambda x: x * 2, [])), [])))(None)
(lambda _: kAssert(kEq(list(filter(lambda x: x > 0, [])), [])))(None)
(lambda _: kAssert(kEq((x := 10, list(map(lambda y: x + y, [1, 2, 3])))[-1], [11, 12, 13])))(None)
