(lambda _: kAssert(list(reversed([1, 2, 3])) == [3, 2, 1]))(None)
(lambda _: kAssert(list(reversed([])) == []))(None)
(lambda _: kAssert(list(reversed(["a", "b", "c"])) == ["c", "b", "a"]))(None)
(lambda _: kAssert(list(reversed([1])) == [1]))(None)
(lambda _: kAssert(",".join(["a", "b", "c"]) == "a,b,c"))(None)
(lambda _: kAssert(" ".join(["hello", "world"]) == "hello world"))(None)
(lambda _: kAssert("-".join(["one"]) == "one"))(None)
(lambda _: kAssert(",".join([]) == ""))(None)
(lambda _: kAssert(kSplit("a,b,c", ",") == ["a", "b", "c"]))(None)
(lambda _: kAssert(kSplit("hello world", " ") == ["hello", "world"]))(None)
(lambda _: kAssert(kSplit("one", ",") == ["one"]))(None)
(lambda _: kAssert(kSplit("", ",") == []))(None)
